import React from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";
function NavItem(props) {
  const { label, link, click, active } = props;
  return (
    <Link to={`/${link}`}>
      <li
        onClick={click}
        className={`flex flex-col gap-1 dark:hover:bg-green-400 hover:bg-green-200  hover:text-green-900  p-2 rounded-lg ${
          active === label
            ? " font-bold text-green-900 dark:bg-green-400 bg-green-200"
            : "font-normal dark:text-gray-300 text-green-900"
        }`}
      >
        {label}
      </li>
    </Link>
  );
}
export default function Header() {
  const [active, setActive] = React.useState("Work");
  console.log(active);
  return (
    <header
      className={
        "mt-6 w-full flex-col sm:flex-row gap-8 sm:gap-0 flex items-center justify-between"
      }
    >
      <div style={{ height: 60, width: 60 }}>
        <svg
          width="60"
          height="60"
          viewBox="0 0 60 60"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <rect
            x="2"
            y="2"
            width="56"
            height="56"
            stroke="rgba(16, 185, 129, 1)"
            stroke-width="4"
          />
          <path
            d="M37.1155 21.0457H33.4422V32.023H33.2888L25.7547 21.0457H22.5161V38.5002H26.2064V27.5144H26.3343L33.928 38.5002H37.1155V21.0457Z"
            fill="rgba(16, 185, 129, 1)"
          />
        </svg>
      </div>
      <nav>
        <ul className={"flex content-center gap-8 sm:gap-16 justify-center"}>
          <NavItem
            click={() => {
              setActive("Work");
            }}
            label={"Work"}
            link={""}
            active={active}
          />
          <NavItem
            click={() => {
              setActive("Experiments");
            }}
            label={"Experiments"}
            link={"experiments"}
            active={active}
          />
          <NavItem
            click={() => {
              setActive("About");
            }}
            label={"About"}
            link={"about"}
            active={active}
          />
          <NavItem
            click={() => {
              setActive("Resume");
            }}
            label={"Resume"}
            link={"resume"}
            active={active}
          />
        </ul>
      </nav>
    </header>
  );
}
