import React from "react";
function NavItem(props) {
  const { label, active } = props;
  const [hover, setHover] = React.useState(false);
  return (
    <li
      onMouseEnter={(e) => setHover(true)}
      className={
        active
          ? "flex flex-col gap-1 font-bold text-gray-700"
          : "flex flex-col gap-1 font-normal text-gray-500"
      }
    >
      <a href={`./${label.toLowerCase()}`}>{label}</a>
      <div
        className={active || hover ? "bg-green-500" : ""}
        style={{ height: 2, width: "100%" }}
      ></div>
    </li>
  );
}
export default function Header() {
  return (
    <header
      className={
        "mt-6 w-full flex-col sm:flex-row gap-8 sm:gap-0 flex items-center justify-between"
      }
    >
      <div style={{ height: 60, width: 60 }}>
        <svg
          width="60"
          height="60"
          viewBox="0 0 60 60"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <rect
            x="2"
            y="2"
            width="56"
            height="56"
            stroke="rgba(16, 185, 129, 1)"
            stroke-width="4"
          />
          <path
            d="M37.1155 21.0457H33.4422V32.023H33.2888L25.7547 21.0457H22.5161V38.5002H26.2064V27.5144H26.3343L33.928 38.5002H37.1155V21.0457Z"
            fill="rgba(16, 185, 129, 1)"
          />
        </svg>
      </div>
      <nav>
        <ul className={"flex content-center gap-8 sm:gap-16 justify-center"}>
          <NavItem label={"Work"} active={true} />
          <NavItem label={"Experiments"} active={false} />
          <NavItem label={"About"} active={false} />
          <NavItem label={"Resume"} active={false} />
        </ul>
      </nav>
    </header>
  );
}
